import * as extensionApi from '@tmpwip/extension-api';

export async function activate(extensionContext: extensionApi.ExtensionContext): Promise<void> {
  // Create the "provider"
  const provider = extensionApi.provider.createProvider({
    name: 'Registries',
    id: 'registries',
    status: 'unknown',
    images: {
      icon: './icon.png',
      logo: './icon.png',
    },
  });
  extensionContext.subscriptions.push(provider);

  // For each defaultRegistries, suggest the registry to Podman Desktop
  defaultRegistries.forEach(registry => {
    extensionApi.registry.suggestRegistry(registry);
  });
}

// Const array of list of approved registries that contain the default URL as well as base64 encoded version of their logo
// Name
// URL
// Base64 png of logo (optional), no need to use `fs` filesystem to load + convert if just providing a small base64 image.
// each image is a 40x40px base64 encoded png
const defaultRegistries: extensionApi.RegistrySuggestedProvider[] = [
  {
    name: 'Docker Hub',
    url: 'docker.io',
    icon: '',
  },
  {
    name: 'Red Hat Quay',
    url: 'quay.io',
    icon: '',
  },
  {
    name: 'GitHub',
    url: 'ghcr.io',
    icon: '',
  },
  {
    name: 'Google Container Registry',
    url: 'gcr.io',
    icon: '',
  },
];
